import React, { useState } from "react";

function App() {
  const tasks = [
    { name: "Complete React project", details: "Due tomorrow. Priority high." },
    { name: "Prepare for interview", details: "Review React and JS basics." },
    { name: "Read React docs", details: "Focus on hooks and routing." },
  ];

  const [selectedTask, setSelectedTask] = useState(null);

  return (
    <div id="root">
      <h1>Task Manager</h1>
      <ul>
        {tasks.map((task, index) => {
          const isExpanded = selectedTask === index;
          return (
            <li
              key={index}
              className={isExpanded ? "task-expanded" : ""}
              onClick={() =>
                setSelectedTask(isExpanded ? null : index)
              }
            >
              <div className="task-title">{task.name}</div>
              {isExpanded && (
                <div className="task-details">
                  <p>{task.details}</p>
                  <form
                    onClick={(e) => e.stopPropagation()} // Prevent toggling when clicking inside form
                    onSubmit={(e) => {
                      e.preventDefault();
                      alert("Add Task form submitted!");
                    }}
                  >
                    <input type="text" placeholder="Add new subtask" />
                    <button type="submit">Add</button>
                  </form>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
