import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./App.css";

function Home() {
  return (
    <div className="page-container">
      <h2>Welcome to the Task Manager App</h2>
      <p>Select Tasks or Calendar from the menu.</p>
    </div>
  );
}

function Tasks() {
  const tasks = [
    { name: "Complete React project", details: "Due tomorrow. Priority high." },
    { name: "Prepare for interview", details: "Review React and JS basics." },
    { name: "Read React docs", details: "Focus on hooks and routing." },
  ];

  const [selectedTask, setSelectedTask] = useState(null);
  const [showAddTaskForm, setShowAddTaskForm] = useState(false);

  function handleTaskClick(index) {
    if (selectedTask === index) {
      setSelectedTask(null);
      setShowAddTaskForm(false);
    } else {
      setSelectedTask(index);
      setShowAddTaskForm(true);
    }
  }

  return (
    <div className="page-container">
      {showAddTaskForm && (
        <div className="add-task-form-container">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              alert("New task added!");
            }}
          >
            <input type="text" placeholder="Add new task here..." required />
            <button type="submit">Add Task</button>
            <button
              type="button"
              onClick={() => setShowAddTaskForm(false)}
              className="cancel-btn"
            >
              Cancel
            </button>
          </form>
        </div>
      )}
      <h1>Task Manager</h1>
      <ul>
        {tasks.map((task, index) => {
          const isExpanded = selectedTask === index;
          return (
            <li
              key={index}
              className={isExpanded ? "task-expanded" : ""}
              onClick={() => handleTaskClick(index)}
            >
              <div className="task-title">{task.name}</div>
              {isExpanded && <div className="task-details">{task.details}</div>}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

function Calendar() {
  return (
    <div className="page-container">
      <h1>Calendar</h1>
      <p>This is a placeholder for the calendar page.</p>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div id="root">
        <nav className="nav-bar">
          <Link to="/">Home</Link>
          <Link to="/tasks">Tasks</Link>
          <Link to="/calendar">Calendar</Link>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/tasks" element={<Tasks />} />
          <Route path="/calendar" element={<Calendar />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
