import React, { useState, useEffect } from 'react';
import { openDB } from 'idb';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Initialize IndexedDB
const DB_NAME = 'taskManagerDB';
const STORE_NAME = 'tasks';

async function getDB() {
  return openDB(DB_NAME, 1, {
    upgrade(db) {
      if (!db.objectStoreNames.contains(STORE_NAME)) {
        db.createObjectStore(STORE_NAME, { keyPath: 'id' });
      }
    },
  });
}

// Get all tasks from IndexedDB
async function getTasksFromDB() {
  const db = await getDB();
  return db.getAll(STORE_NAME);
}

// Add or update a task
async function saveTaskToDB(task) {
  const db = await getDB();
  await db.put(STORE_NAME, task);
}

// Delete task from DB
async function deleteTaskFromDB(id) {
  const db = await getDB();
  await db.delete(STORE_NAME, id);
}

// TaskPage Component - Add/Delete Tasks
function TaskPage({ tasks, setTasks }) {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');

  const addTask = async () => {
    if (!title || !date) return alert('Please enter title and date');
    const newTask = { id: Date.now(), title, date };
    setTasks(prev => [...prev, newTask]);
    await saveTaskToDB(newTask);
    setTitle('');
    setDate('');
  };

  const deleteTask = async (id) => {
    setTasks(prev => prev.filter(task => task.id !== id));
    await deleteTaskFromDB(id);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Add Task</h2>
      <input
        placeholder="Task title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <input
        type="date"
        value={date}
        onChange={e => setDate(e.target.value)}
      />
      <button onClick={addTask}>Add Task</button>

      <h3>Tasks List</h3>
      {tasks.length === 0 && <p>No tasks added.</p>}
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.title} â€” {task.date}
            <button onClick={() => deleteTask(task.id)} style={{ marginLeft: 10 }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

// SchedulePage Component - Show Tasks in Calendar Style
function SchedulePage({ tasks }) {
  const sortedTasks = [...tasks].sort((a, b) => a.date.localeCompare(b.date));

  return (
    <div style={{ padding: '20px' }}>
      <h2>Schedule</h2>
      {sortedTasks.length === 0 && <p>No tasks scheduled.</p>}
      <ul>
        {sortedTasks.map(task => (
          <li key={task.id}>
            <strong>{task.date}:</strong> {task.title}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [tasks, setTasks] = useState([]);

  // Load tasks from IndexedDB on app load
  useEffect(() => {
    async function loadTasks() {
      const allTasks = await getTasksFromDB();
      setTasks(allTasks);
    }
    loadTasks();
  }, []);

  return (
    <Router>
      <nav style={{ padding: '10px', background: '#282c34', color: 'white' }}>
        <Link to="/" style={{ margin: '0 10px', color: 'white' }}>Tasks</Link>
        <Link to="/schedule" style={{ margin: '0 10px', color: 'white' }}>Schedule</Link>
      </nav>

      <Routes>
        <Route path="/" element={<TaskPage tasks={tasks} setTasks={setTasks} />} />
        <Route path="/schedule" element={<SchedulePage tasks={tasks} />} />
      </Routes>
    </Router>
  );
}

export default App;
